mapper.select:
SELECT sum( case when t.JOB_TYPE=1 THEN 1 ELSE 0 END ) full_time_job_count, sum( CASE WHEN t.JOB_TYPE = 1 AND t.STAFF_SCOPE = 1 THEN 1 ELSE 0 END ) on_job_count, count( DISTINCT ( t.post_id ) ) post_count, 0 trail_count, 0 establish_count from ( SELECT s.JOB_TYPE, s.STAFF_SCOPE, u.post_id, 0 trail_count, 0 establish_count FROM mp_organization a INNER JOIN mp_userpost u ON u.ORG_DEPT_ID = a.ID AND u.MAIN_POST = 1 INNER JOIN mp_staff s ON u.USER_ID = s.USER_ID WHERE 1 =1
AND a.ORG_CODE LIKE concat(#{orgCode},'%')
) t

SELECT t.less_than_20, ROUND(t.less_than_20/t.total,2)*100 less_than_20_zb, t.in_20_30, ROUND(t.in_20_30/t.total,2)*100 in_20_30_zb, t.in_30_40, ROUND(t.in_30_40/t.total,2)*100 in_30_40_zb, t.large_than_40, ROUND(t.large_than_40/t.total,2)*100 large_than_40_zb from ( SELECT SUM(case when t.age < 20 then 1 else 0 end ) less_than_20, SUM(case when t.age >=20 and t.age <= 30 then 1 else 0 end ) in_20_30, SUM(case when t.age >=30 and t.age <= 40 then 1 else 0 end ) in_30_40, SUM(case when t.age >=40 then 1 else 0 end ) large_than_40, COUNT(t.staff_id) total FROM ( SELECT s.id staff_id, DATE_FORMAT(FROM_DAYS(TO_DAYS(NOW())-TO_DAYS(s.BIRTHDAY)), '%Y')+0 AS age FROM mp_organization a INNER JOIN mp_userpost u ON u.ORG_DEPT_ID = a.ID AND u.MAIN_POST = 1 INNER JOIN mp_staff s ON u.USER_ID = s.USER_ID WHERE 1=1
AND a.ORG_CODE LIKE concat(#{orgCode},'%')
) t )t
