mapper.select:
SELECT t.full_time_job_count, ROUND(t.full_time_job_count/t.job_type_total,2)*100 full_time_job_zb, t.trainee_job_count, ROUND(t.trainee_job_count/t.job_type_total,2)*100 trainee_job_zb, t.part_time_job_count, ROUND(t.part_time_job_count/t.job_type_total,2)*100 part_time_job_zb, t.labor_job_count, ROUND(t.labor_job_count/t.job_type_total,2)*100 labor_job_zb, t.outer_job_count, ROUND(t.outer_job_count/t.job_type_total,2)*100 outer_job_zb, t.retire_job_count, ROUND(t.retire_job_count/t.job_type_total,2)*100 retire_job_zb from ( SELECT sum( t.full_time_job_count ) full_time_job_count, sum( t.trainee_job_count ) trainee_job_count, sum( t.part_time_job_count ) part_time_job_count, sum( t.labor_job_count ) labor_job_count, sum( t.outer_job_count ) outer_job_count, sum( t.retire_job_count ) retire_job_count, sum( t.full_time_job_count + t.trainee_job_count + t.part_time_job_count + t.labor_job_count + t.outer_job_count + t.retire_job_count ) job_type_total FROM ( SELECT sum( CASE WHEN t.JOB_TYPE = 1 THEN 1 ELSE 0 END ) full_time_job_count, -- 全职 sum( CASE WHEN t.JOB_TYPE = 5 THEN 1 ELSE 0 END ) trainee_job_count, -- 实习生 sum( CASE WHEN t.JOB_TYPE = 2 THEN 1 ELSE 0 END ) part_time_job_count, -- 兼职 sum( CASE WHEN t.JOB_TYPE = 6 THEN 1 ELSE 0 END ) labor_job_count, -- 劳务派遣 sum( CASE WHEN t.JOB_TYPE = 7 THEN 1 ELSE 0 END ) outer_job_count, -- 外包 sum( CASE WHEN t.JOB_TYPE = 8 THEN 1 ELSE 0 END ) retire_job_count -- 退休返聘 FROM ( SELECT s.JOB_TYPE FROM mp_staff s LEFT JOIN mp_user u ON s.user_id = u.ID LEFT JOIN mp_userpost up ON s.USER_ID = up.USER_ID AND up.MAIN_POST = 1 LEFT JOIN mp_post p ON p.ID = up.POST_ID LEFT JOIN mp_group g ON p.GROUP_ID = g.ID LEFT JOIN mp_organization o ON u.ORG_DEPT_ID = o.ID
AND o.ORG_CODE LIKE concat(#{orgCode},'%')
) t ) t )t

SELECT t.less_than_20, ROUND(t.less_than_20/t.total,2)*100 less_than_20_zb, t.in_20_30, ROUND(t.in_20_30/t.total,2)*100 in_20_30_zb, t.in_30_40, ROUND(t.in_30_40/t.total,2)*100 in_30_40_zb, t.large_than_40, ROUND(t.large_than_40/t.total,2)*100 large_than_40_zb from ( SELECT SUM(case when t.age <20 then 1 else 0 end ) less_than_20, SUM(case when t.age >=20 and t.age <= 30 then 1 else 0 end ) in_20_30, SUM(case when t.age >=30 and t.age <= 40 then 1 else 0 end ) in_30_40, SUM(case when t.age >=40 then 1 else 0 end ) large_than_40, COUNT(t.staff_id) total FROM ( SELECT s.id staff_id, DATE_FORMAT( FROM_DAYS( TO_DAYS( NOW())- TO_DAYS( s.BIRTHDAY )), '%Y' )+ 0 AS age FROM mp_staff s LEFT JOIN mp_user u ON s.user_id = u.ID LEFT JOIN mp_userpost up ON s.USER_ID = up.USER_ID AND up.MAIN_POST = 1 LEFT JOIN mp_post p ON p.ID = up.POST_ID LEFT JOIN mp_group g ON p.GROUP_ID = g.ID LEFT JOIN mp_organization o ON u.ORG_DEPT_ID = o.ID
AND o.ORG_CODE LIKE concat(#{orgCode},'%')
) t )t

SELECT t.POST_NAME, count(t.staff_id) post_count, ROUND(count(t.staff_id)/t.post_total,2)*100 post_zb FROM ( SELECT p.ID post_id, p.POST_NAME, s.id staff_id, ( SELECT count(s.id) post_total FROM mp_staff s LEFT JOIN mp_user u ON s.user_id = u.ID LEFT JOIN mp_userpost up ON s.USER_ID = up.USER_ID AND up.MAIN_POST = 1 LEFT JOIN mp_post p ON p.ID = up.POST_ID LEFT JOIN mp_group g ON p.GROUP_ID = g.ID LEFT JOIN mp_organization o ON u.ORG_DEPT_ID = o.ID
AND o.ORG_CODE LIKE concat(#{orgCode},'%')
) post_total FROM mp_staff s LEFT JOIN mp_user u ON s.user_id = u.ID LEFT JOIN mp_userpost up ON s.USER_ID = up.USER_ID AND up.MAIN_POST = 1 LEFT JOIN mp_post p ON p.ID = up.POST_ID LEFT JOIN mp_group g ON p.GROUP_ID = g.ID LEFT JOIN mp_organization o ON u.ORG_DEPT_ID = o.ID
AND o.ORG_CODE LIKE concat(#{orgCode},'%')
) t GROUP BY t.post_id, t.POST_NAME
