mapper.select:
select * from mp_post where del_flag !=1

    and group_id = #{t.groupId}


    and is_stop = #{t.stop}



    and (POST_CODE like #{likeTxt} or POST_NAME like #{likeTxt})


    AND section_id = #{t.sectionId}


    AND pipeline_id = #{t.pipelineId}


    AND (job_level_id1 = #{t.jobLevelId1}
    or job_level_id2 = #{t.jobLevelId1})

select count(*) from mp_post p, mp_userpost up , mp_staff s
where p.id = up.POST_ID and s.user_id = up.USER_ID and up.USER_ID = s.user_id
and s.STAFF_SCOPE = '1' and p.id =#{id}

select count(*) from mp_post p, mp_userpost up , mp_staff s
where p.id = up.POST_ID and s.user_id = up.USER_ID and up.USER_ID = s.user_id
${condition}
