mapper.select:
select * from mp_post where del_flag !=1


    and id = #{post.id}


    and is_stop = #{post.stop}


    and group_id = #{post.groupId}



    and concat(POST_CODE,POST_NAME) like #{likeTxt}



    AND group_id = #{post.groupId}


    AND section_id = #{post.sectionId}


    AND pipeline_id = #{post.pipelineId}


    AND (job_level_id1 = #{post.jobLevelId1}
    or job_level_id2 = #{post.jobLevelId1})

select count(*) from mp_post p, mp_userpost up , mp_staff s
where p.id = up.POST_ID and s.user_id = up.USER_ID and up.USER_ID = s.user_id
and s.STAFF_SCOPE = '1' and p.id =#{id}

select count(*) from mp_post p, mp_userpost up , mp_staff s
where p.id = up.POST_ID and s.user_id = up.USER_ID and up.USER_ID = s.user_id
${condition}
