mapper.select:
select * from ( SELECT app.id, app.user_name, app.staff_id, app.mobile_no, org.org_name, pst.post_name, ryxz.value personnel_nature, rzlx.value office_type, spzt.value status, CONCAT(app.creator_name, " 于 ", app.creator_time, " 创建" ) create_desc, CONCAT(app.modifier_name, " 于 ", app.modifier_time, " 更新" ) update_desc FROM mp_staff_entrypost_approve app LEFT JOIN mp_organization org on org.id=app.org_id LEFT JOIN mp_post pst on pst.id= app.post_id LEFT JOIN sys_dict_item ryxz on ryxz.type='RYXZ' and ryxz.value=app.personnel_nature LEFT JOIN sys_dict_item rzlx on rzlx.type='JOB_TYPE' and rzlx.value=app.office_type LEFT JOIN sys_dict_item spzt on spzt.type='YWDZT' and spzt.value=app.status
and app.del_flag = 0
and app.org_id= #{orgId}
and app.status= #{status}
)t
and CONCAT( IFNULL(t.user_name,''), IFNULL(t.staff_id,''), IFNULL(t.mobile_no,''), IFNULL(t.org_name,''), IFNULL(t.post_name,''), IFNULL(t.personnel_nature,''), IFNULL(t.office_type,''), IFNULL(t.status,''), IFNULL(t.create_desc,''), IFNULL(t.update_desc,'') ) LIKE concat('%', #{searchText} ,'%')

SELECT t.staff_code FROM ( SELECT app.staff_code FROM mp_recruitment rec, mp_staff_entrypost_approve app WHERE rec.id = app.recruitment_id AND app.entry_status = 'RZZT03'
AND rec.idnumber = #{idnumber}
ORDER BY app.creator_time DESC ) t LIMIT 1

SELECT t.group_code, t.group_name, t.dept_code, t.staff_code_head FROM ( SELECT 'JT001' group_code, '' dept_code, '股份集团' group_name,'2' staff_code_head UNION ALL SELECT 'JT002' group_code, '' dept_code, '珠控集团（风控）' group_name, '3' staff_code_head UNION ALL SELECT 'JT019' group_code, '' dept_code ,'珠江金融' group_name,'31' staff_code_head UNION ALL SELECT 'JT003' group_code, '003012' dept_code ,'物业' group_name,'5' staff_code_head UNION ALL SELECT 'JT003' group_code, '' dept_code , '合生集团' group_name,'6' staff_code_head UNION ALL SELECT 'JT020' group_code, '' dept_code , '汽车租赁集团' group_name,'32' staff_code_head UNION ALL SELECT 'JT004' group_code, '' dept_code , '商贸集团' group_name,'50' staff_code_head UNION ALL SELECT 'JT005' group_code, '' dept_code , '投管集团' group_name,'9' staff_code_head UNION ALL SELECT 'JT006' group_code, '' dept_code , '医疗集团' group_name,'6' staff_code_head UNION ALL SELECT 'JT007' group_code, '' dept_code , '教育集团' group_name, '7' staff_code_head UNION ALL SELECT 'JT008' group_code, '' dept_code , '保险集团' group_name,'8' staff_code_head UNION ALL SELECT 'JT009' group_code, '' dept_code , '至诚伟业' group_name, '11' staff_code_head UNION ALL SELECT 'JT010' group_code, '' dept_code , '德润公司' group_name, '12' staff_code_head UNION ALL SELECT 'JT011' group_code, '' dept_code , '韩建公司' group_name,'13' staff_code_head UNION ALL SELECT 'JT012' group_code, '' dept_code , '商管集团' group_name,'80' staff_code_head UNION ALL SELECT 'JT013' group_code, '' dept_code , '基建集团' group_name,'15' staff_code_head UNION ALL SELECT 'JT014' group_code, '' dept_code , '基金公司' group_name, '16' staff_code_head UNION ALL SELECT 'JT015' group_code, '' dept_code , '合美国际教育集团' group_name, '7' staff_code_head UNION ALL SELECT 'JT016' group_code, '' dept_code , '设计集团' group_name,'17' staff_code_head UNION ALL SELECT 'JT017' group_code, '' dept_code , '珠江金融' group_name, '18' staff_code_head UNION ALL SELECT 'JT018' group_code, '' dept_code , '好生活' group_name, '19' staff_code_head UNION ALL SELECT 'JT021' group_code, '' dept_code , '超竞' group_name, '33' staff_code_head UNION ALL SELECT 'JT022' group_code, '' dept_code , '城更集团' group_name, '35' staff_code_head UNION ALL SELECT 'JT023' group_code, '' dept_code , '元知科技集团' group_name, '66' staff_code_head UNION ALL SELECT 'JT024' group_code, '' dept_code , '外部人员' group_name, '67' staff_code_head ) t
AND t.group_code = #{idnumber}
AND t.dept_code = #{deptCode}

select t.* from ( SELECT app.id, app.user_name, app.mobile_no, app.email, org.org_name, app.certificate_no, pst.post_name, jl.JOB_LEVEL_NAME job_level_name, app.entry_post_time, rzdjqk.`value` entry_login_status, CONCAT( app.modifier_name, " 于 ", app.modifier_time, " 更新" ) update_desc, IF(invite_count>0,'已邀请','未邀请') invite_status FROM mp_staff_entrypost_approve app LEFT JOIN mp_organization org ON org.id = app.org_id LEFT JOIN mp_post pst ON pst.id = app.post_id LEFT JOIN mp_job_level jl ON jl.id=app.job_level_id LEFT JOIN sys_dict_item rzdjqk on rzdjqk.type='RZDJQK' and rzdjqk.value=app.entry_login_status LEFT JOIN ( SELECT inv.staff_entrypost_approve_id, count( inv.staff_entrypost_approve_id ) invite_count FROM mp_staff_entrypost_invite inv WHERE inv.del_flag =0 GROUP BY staff_entrypost_approve_id ) invr on app.id = invr.staff_entrypost_approve_id
app.STATUS = 3 AND app.entry_status = 'RZZT01' AND app.del_flag = 0
AND app.org_id= #{orgId}
) t
and CONCAT( IFNULL(t.user_name,''), IFNULL(t.mobile_no,''), IFNULL(t.email,''), IFNULL(t.org_name,''), IFNULL(t.post_name,''), IFNULL(t.job_level_name,''), IFNULL(t.entry_post_time,''), IFNULL(t.entry_login_status,''), IFNULL(t.update_desc,''), IFNULL(t.invite_status,'') ) LIKE concat('%', #{searchText} ,'%')

select * from ( SELECT app.id, app.user_name, app.staff_code, app.mobile_no, app.certificate_no, app.email, org.org_name, pst.post_name, jl.JOB_LEVEL_NAME job_level_name, app.entry_post_time, rzdjqk.`value` entry_login_status, rygsfw.value staff_status, rzlx.value office_type, app.probation, jb.job_count, CONCAT( app.modifier_name, " 于 ", app.modifier_time, " 更新" ) update_desc, IF(invite_count>0,'已邀请','未邀请') invite_status FROM mp_staff_entrypost_approve app LEFT JOIN mp_organization org ON org.id = app.org_id LEFT JOIN mp_post pst ON pst.id = app.post_id LEFT JOIN mp_job_level jl ON jl.id=app.job_level_id LEFT JOIN sys_dict_item rzdjqk on rzdjqk.type='RZDJQK' and rzdjqk.value=app.entry_login_status LEFT JOIN sys_dict_item rygsfw on rygsfw.type='RYGSFW' and rygsfw.value=app.staff_status LEFT JOIN sys_dict_item rzlx on rzlx.type='JOB_TYPE' and rzlx.value=app.office_type LEFT JOIN ( SELECT inv.staff_entrypost_approve_id, count( inv.staff_entrypost_approve_id ) invite_count FROM mp_staff_entrypost_invite inv WHERE inv.del_flag =0 GROUP BY staff_entrypost_approve_id ) invr on app.id = invr.staff_entrypost_approve_id LEFT JOIN ( SELECT e.recruitment_id, count(e.id) job_count FROM mp_staff_entrypost_approve e WHERE e.STATUS = 3 AND e.entry_status = 'RZZT02' AND e.del_flag = 0 GROUP BY e.recruitment_id ) jb on app.recruitment_id = jb.recruitment_id
AND app.STATUS = 3 AND app.entry_status = 'RZZT02' AND app.del_flag = 0 AND DATE_SUB( CURDATE(), INTERVAL 30 DAY ) <= date(app.entry_post_time )
AND app.org_id= #{orgId}
) t
and CONCAT( IFNULL(t.user_name,''), IFNULL(t.staff_code,''), IFNULL(t.mobile_no,''), IFNULL(t.certificate_no,''), IFNULL(t.email,''), IFNULL(t.post_name,''), IFNULL(t.job_level_name,''), IFNULL(t.entry_post_time,''), IFNULL(t.entry_login_status,''), IFNULL(t.staff_status,''), IFNULL(t.probation,''), IFNULL(t.update_desc,''), IFNULL(t.invite_status,'') ) LIKE concat('%', #{searchText} ,'%')
