mapper.select:
SELECT mslpa.id, mslpa.user_id, s.staff_name, s.staff_code, s.job_type, s.entry_time, s.company_seniority, mslpa.leave_time, mslpa.leave_reason, norg.ORG_NAME now_org_name, np.POST_NAME now_post_name, njl.JOB_LEVEL_NAME now_job_level_name
FROM mp_staff_leave_post_approve mslpa LEFT JOIN mp_staff s ON mslpa.user_id = s.user_id LEFT JOIN mp_organization norg ON mslpa.now_org_id = norg.id LEFT JOIN mp_post np ON mslpa.now_post_id = np.id LEFT JOIN mp_job_level njl ON mslpa.now_job_level_id = njl.id
and concat( IFNULL(s.staff_name,''), IFNULL(s.staff_code,''), IFNULL(mslpa.job_type,''), IFNULL(mslpa.company_seniority,''), IFNULL(mslpa.leave_reason,''), IFNULL(mslpa.trans_start_date,''), IFNULL(norg.ORG_NAME,''), IFNULL(np.POST_NAME,''), IFNULL(njl.JOB_LEVEL_NAME,''), ) LIKE concat(#{searchText},'%')
and mslpa.`status` = '5' or mslpa.`status` = '6'
and mslpa.`status` = #{status}
and norg.`org_id` = #{orgId}

SELECT sta.id, sta.user_id, s.staff_name, s.staff_code, sta.trans_start_date, norg.ORG_NAME now_org_name, np.POST_NAME now_post_name, torg.ORG_NAME trans_org_name, tp.POST_NAME trans_post_name, njl.JOB_LEVEL_NAME now_job_level_name , tjl.JOB_LEVEL_NAME trans_job_level_name
FROM mp_staff_transfer_approve sta LEFT JOIN mp_staff s ON sta.user_id = s.user_id LEFT JOIN mp_organization norg ON sta.now_org_id = norg.id LEFT JOIN mp_post np ON sta.now_post_id = np.id LEFT JOIN mp_organization torg ON sta.trans_org_id = torg.id LEFT JOIN mp_post tp ON sta.trans_post_id = tp.id LEFT JOIN mp_job_level njl ON sta.now_job_level_id = njl.id LEFT JOIN mp_job_level tjl ON sta.trans_job_level_id = tjl.id
and transfer_type = 'CALL_IN_AND_CALL_OUT'
and concat( IFNULL(s.staff_name,''), IFNULL(s.staff_code,''), IFNULL(sta.trans_start_date,''), IFNULL(norg.ORG_NAME,''), IFNULL(np.POST_NAME,''), IFNULL(torg.ORG_NAME,''), IFNULL(tp.POST_NAME,''), IFNULL(njl.JOB_LEVEL_NAME,''), IFNULL(tjl.JOB_LEVEL_NAME,'') ) LIKE concat(#{searchText},'%')
and sta.`status` = #{status}
and norg.`org_id` = #{orgId}
