mapper.select:
SELECT t.id, t.staff_name, t.staff_code, t.BEGIN_DATE, t.END_DATE, t.staff_id, t.SCHOOL_NAME, t.PROFESSIONAL, t.EDUCATION_DEGREE, t.EDUCATION_QUA, t.LEARN_FORM, t.modifier_name, t.MODIFIED_TIME, CONCAT( t.modifier_name, " 于 ", t.MODIFIED_TIME, " 更新" ) updateDesc FROM ( SELECT e.id, s.staff_name, s.staff_code, e.BEGIN_DATE, e.END_DATE, e.staff_id, e.SCHOOL_NAME, e.PROFESSIONAL, dic.label EDUCATION_DEGREE, di.label EDUCATION_QUA, dd.label LEARN_FORM, usr.USER_NAME modifier_name, e.MODIFIED_TIME FROM mp_staffeducation e LEFT JOIN mp_staff s ON s.ID = e.STAFF_ID LEFT JOIN mp_userpost u ON u.USER_ID = s.USER_ID AND u.MAIN_POST = 1 LEFT JOIN sys_dict_item dic ON dic.type = 'EDUCATION_DEGREE_TYPE' AND dic.`value` = e.EDUCATION_DEGREE LEFT JOIN sys_dict_item di ON di.type = 'EDUCATION_QUA_TYPE' AND di.`value` = e.EDUCATION_QUA LEFT JOIN sys_dict_item dd ON dd.type = 'XXXS' AND dd.`value` = e.LEARN_FORM LEFT JOIN mp_user usr ON usr.id = e.MODIFIER_CODE WHERE 1 = 1 AND e.del_flag = 0
and t.staff_id = #{staffId}
) t WHERE 1 = 1
and CONCAT( IFNULL(t.staff_name,''), IFNULL(t.staff_code,''),IFNULL(t.BEGIN_DATE,'') ,IFNULL(t.END_DATE,''), IFNULL(t.SCHOOL_NAME,'') ,IFNULL(t.PROFESSIONAL,'') ,IFNULL(t.EDUCATION_DEGREE,'') , IFNULL(t.LEARN_FORM,'') ,IFNULL(t.EDUCATION_QUA,'') ,IFNULL(t.modifier_name,''), IFNULL(t.modifier_name,''), IFNULL(t.MODIFIED_TIME,'') ) LIKE concat('%',#{searchText},'%')

SELECT t.id, t.staff_name, t.staff_code, t.BEGIN_DATE, t.END_DATE, t.staff_id, t.SCHOOL_NAME, t.PROFESSIONAL, t.EDUCATION_DEGREE, t.EDUCATION_QUA, t.LEARN_FORM, t.modifier_name, t.MODIFIED_TIME, CONCAT( t.modifier_name, " 于 ", t.MODIFIED_TIME, " 更新" ) updateDesc FROM ( SELECT e.id, s.staff_name, s.staff_code, e.BEGIN_DATE, e.END_DATE, e.staff_id, e.SCHOOL_NAME, e.PROFESSIONAL, dic.label EDUCATION_DEGREE, di.label EDUCATION_QUA, dd.label LEARN_FORM, usr.USER_NAME modifier_name, e.MODIFIED_TIME FROM mp_staffeducation e LEFT JOIN mp_staff s ON s.ID = e.STAFF_ID LEFT JOIN mp_userpost u ON u.USER_ID = s.USER_ID AND u.MAIN_POST = 1 LEFT JOIN sys_dict_item dic ON dic.type = 'EDUCATION_DEGREE_TYPE' AND dic.`value` = e.EDUCATION_DEGREE LEFT JOIN sys_dict_item di ON di.type = 'EDUCATION_QUA_TYPE' AND di.`value` = e.EDUCATION_QUA LEFT JOIN sys_dict_item dd ON dd.type = 'XXXS' AND dd.`value` = e.LEARN_FORM LEFT JOIN mp_user usr ON usr.id = e.MODIFIER_CODE WHERE 1 = 1 AND e.del_flag = 0
and t.staff_id = #{staffId}
) t WHERE 1 = 1
and CONCAT( IFNULL(t.staff_name,''), IFNULL(t.staff_code,''),IFNULL(t.BEGIN_DATE,'') ,IFNULL(t.END_DATE,''), IFNULL(t.SCHOOL_NAME,'') ,IFNULL(t.PROFESSIONAL,'') ,IFNULL(t.EDUCATION_DEGREE,'') , IFNULL(t.LEARN_FORM,'') ,IFNULL(t.EDUCATION_QUA,'') ,IFNULL(t.modifier_name,''), IFNULL(t.modifier_name,''), IFNULL(t.MODIFIED_TIME,'') ) LIKE concat('%',#{searchText},'%')
