mapper.select:
select * from ( SELECT e.id, s.staff_name, s.staff_code, e.BEGIN_DATE, e.END_DATE, e.staff_id, e.SCHOOL_NAME, e.PROFESSIONAL, ( SELECT d.label FROM sys_dict_item d WHERE d.type = 'EDUCATION_DEGREE_TYPE' AND d.`value` = e.EDUCATION_DEGREE ) EDUCATION_DEGREE, ( SELECT d.label FROM sys_dict_item d WHERE d.type = 'EDUCATION_QUA_TYPE' AND d.`value` = e.EDUCATION_QUA ) EDUCATION_QUA, e.LEARN_FORM, ( SELECT u.USER_NAME FROM mp_user u WHERE u.id = e.MODIFIER_CODE ) modifier_name, e.MODIFIED_TIME, ( SELECT u.USER_NAME FROM mp_user u WHERE u.id = e.CREATOR_CODE ) create_name, e.CREATED_TIME FROM mp_staffeducation e LEFT JOIN mp_staff s ON s.ID = e.STAFF_ID LEFT JOIN mp_userpost u ON u.USER_ID = s.USER_ID AND u.MAIN_POST = 1 WHERE 1 = 1 AND e.del_flag = 0 )t where 1=1
and t.staff_id = #{staffId}
and CONCAT( t.staff_name, t.staff_code,t.BEGIN_DATE,t.END_DATE, t.SCHOOL_NAME,t.PROFESSIONAL,t.EDUCATION_DEGREE,t.LEARN_FORM, t.EDUCATION_QUA,t.modifier_name,t.modifier_name, t.MODIFIED_TIME, t.create_name,t.CREATED_TIME ) LIKE concat('%',#{searchText},'%')

select * from ( SELECT e.id, s.staff_name, s.staff_code, e.BEGIN_DATE, e.END_DATE, e.staff_id, e.SCHOOL_NAME, e.PROFESSIONAL, ( SELECT d.label FROM sys_dict_item d WHERE d.type = 'EDUCATION_DEGREE_TYPE' AND d.`value` = e.EDUCATION_DEGREE ) EDUCATION_DEGREE, ( SELECT d.label FROM sys_dict_item d WHERE d.type = 'EDUCATION_QUA_TYPE' AND d.`value` = e.EDUCATION_QUA ) EDUCATION_QUA, e.LEARN_FORM, ( SELECT u.USER_NAME FROM mp_user u WHERE u.id = e.MODIFIER_CODE ) modifier_name, e.MODIFIED_TIME, ( SELECT u.USER_NAME FROM mp_user u WHERE u.id = e.CREATOR_CODE ) create_name, e.CREATED_TIME FROM mp_staffeducation e LEFT JOIN mp_staff s ON s.ID = e.STAFF_ID LEFT JOIN mp_userpost u ON u.USER_ID = s.USER_ID AND u.MAIN_POST = 1 WHERE 1 = 1 AND e.del_flag = 0 )t where 1=1
and t.staff_id = #{staffId}
and CONCAT( t.staff_name, t.staff_code,t.BEGIN_DATE,t.END_DATE, t.SCHOOL_NAME,t.PROFESSIONAL,t.EDUCATION_DEGREE,t.LEARN_FORM, t.EDUCATION_QUA,t.modifier_name,t.modifier_name, t.MODIFIED_TIME, t.create_name,t.CREATED_TIME ) LIKE concat('%',#{searchText},'%')
