mapper.select:
select * from ( SELECT f.id, s.staff_name, s.staff_code, f.TRAN_TIME, f.TRAN_TYPE, f.TRAN_REASON, f.OUT_UNIT_NAME, f.COME_UNIT_NAME, ( SELECT u.USER_NAME FROM mp_user u WHERE u.id = f.MODIFIED_CODE) MODIFIEd_NAME, f.MODIFIED_TIME, f.STAFF_ID FROM mp_stafftransaction f LEFT JOIN mp_staff s ON s.ID = f.STAFF_id LEFT JOIN mp_userpost u on u.USER_ID =s.USER_ID AND u.MAIN_POST = 1 where 1=1 and f.DEL_FLAG=0 )t where 1=1
t.staffId=#{staffId}
and CONCAT( IFNULL(t.staff_name,'') , IFNULL(t.staff_code,''),IFNULL(t.TRAN_TIME,'') , IFNULL(t.TRAN_TYPE,'') ,IFNULL(t.TRAN_REASON,'') , IFNULL(t.OUT_UNIT_NAME,'') , IFNULL(t.COME_UNIT_NAME,''),IFNULL(t.MODIFIEd_NAME,''), IFNULL(t.MODIFIED_TIME,'') ) LIKE concat('%',#{searchText},'%')

select * from ( SELECT f.id, s.staff_name, s.staff_code, f.TRAN_TIME, f.TRAN_TYPE, f.TRAN_REASON, f.OUT_UNIT_NAME, f.COME_UNIT_NAME, ( SELECT u.USER_NAME FROM mp_user u WHERE u.id = f.MODIFIED_CODE) MODIFIEd_NAME, f.MODIFIED_TIME, f.STAFF_ID FROM mp_stafftransaction f LEFT JOIN mp_staff s ON s.ID = f.STAFF_id LEFT JOIN mp_userpost u on u.USER_ID =s.USER_ID AND u.MAIN_POST = 1 where 1=1 and f.DEL_FLAG=0 )t where 1=1
t.staffId=#{staffId}
and CONCAT( IFNULL(t.staff_name,'') , IFNULL(t.staff_code,''),IFNULL(t.TRAN_TIME,'') , IFNULL(t.TRAN_TYPE,'') ,IFNULL(t.TRAN_REASON,'') , IFNULL(t.OUT_UNIT_NAME,'') , IFNULL(t.COME_UNIT_NAME,''),IFNULL(t.MODIFIEd_NAME,''), IFNULL(t.MODIFIED_TIME,'') ) LIKE concat('%',#{searchText},'%')
