mapper.select:
SELECT S.STAFF_NAME staffName, S.STAFF_CODE staffCode, u.START_DATE startDate, a.ORG_NAME orgName, sec.SECTION_NAME sectionName, pi.PIPELINE_NAME pipelineName, p.POST_NAME postName, jl.JOB_LEVEL_NAME jobLevelCode, di.label customPost FROM mp_userposthistory u INNER JOIN mp_staff s ON u.USER_ID = s.USER_ID LEFT JOIN mp_organization a ON u.ORG_DEPT_ID = a.ID LEFT JOIN mp_post p ON p.ID = u.POST_ID LEFT JOIN mp_section sec ON sec.id = p.SECTION_ID LEFT JOIN mp_pipeline pi ON pi.id = p.PIPELINE_ID LEFT JOIN sys_dict_item di ON di.id = u.office_post_type LEFT JOIN mp_job_level jl ON jl.JOB_LEVEL_CODE = P.JOB_LEVEL_ID WHERE 1 = 1 AND u.del_flag = 0
and s.id = #{staffId}
and u.ORG_DEPT_ID = #{orgId}
and s.staff_name LIKE concat(#{staffName},'%')
and s.staff_code LIKE concat(#{staffCode},'%')

SELECT S.STAFF_NAME staffName, S.STAFF_CODE staffCode, u.START_DATE startDate, a.ORG_NAME orgName, sec.SECTION_NAME sectionName, pi.PIPELINE_NAME pipelineName, p.POST_NAME postName, jl.JOB_LEVEL_NAME jobLevelCode, di.label customPost FROM mp_userposthistory u INNER JOIN mp_staff s ON u.USER_ID = s.USER_ID LEFT JOIN mp_organization a ON u.ORG_DEPT_ID = a.ID LEFT JOIN mp_post p ON p.ID = u.POST_ID LEFT JOIN mp_section sec ON sec.id = p.SECTION_ID LEFT JOIN mp_pipeline pi ON pi.id = p.PIPELINE_ID LEFT JOIN sys_dict_item di ON di.id = u.office_post_type LEFT JOIN mp_job_level jl ON jl.JOB_LEVEL_CODE = P.JOB_LEVEL_ID WHERE 1 = 1 AND u.del_flag = 0
and s.id = #{staffId}
and u.ORG_DEPT_ID = #{orgId}
and s.staff_name LIKE concat(#{staffName},'%')
and s.staff_code LIKE concat(#{staffCode},'%')
